mapper.update:
update tbl_user_register
set user_id=#{userId}, user_name=#{userName}, user_email=#{userEmail}, user_pw=#{userPw}, user_phone_num=#{userPhoneNum}, user_gender=#{userGender}, user_birth=#{userBirth}, user_kakao_email=#{userKakaoEmail}, user_facebook_email=#{userFacebookEmail}
where user_num=#{userNum}

update tbl_user_register
set user_pw = #{userPw}
where user_num = #{userNum}
