mapper.insert:

   insert into tbl_user_write
(title, content, write_date)
values(#{title}, #{content}, #{writeDate});

mapper.update:
update tbl_user_write
set title=#{title}, content = #{content}
where user_write_num = #{userWriteNum}

mapper.select:
select user_write_num, request_num, title, content, write_date
from tbl_user_write
where request_num = #{requestNum}

   select user_write_num, request_num, title, content, write_date
from tbl_user_write
where user_write_num = #{userWriteNum}

select field
from tbl_user_write
where field = #{field}
